    public function getSourceContext()
    {
        return new Source("{% extends 'base.html.twig' %}

{% block title %}Index des Séries{% endblock %}

{% block body %}
    <h1>Index des Séries</h1>

    <table class=\"table\">
        <thead>
            <tr>
                <th>Id</th>
                <th>Titre</th>
                <th>Catégorie</th>
                <th>Affiche</th>
                <th>Pays</th>
                <th>Année</th>
                <th>Description</th>
                <th>Action</th>
            </tr>
        </thead>
        <tbody>
        {% for program in programs %}
            <tr>
                <td>{{ program.id }}</td>
                <td class=\"title\">{{ program.title }}</td>
                <td>{{ program.category.name}}</td>
                <td><img class=\"poster\" src=\"{{ program.poster }}\"</td>
                <td>{{ program.country }}</td>
                <td>{{ program.year }}</td>
                <td class=\"summary\">{{ program.summary }}</td>
                <td>
                    <a href=\"{{ path('program_show', {'id': program.id}) }}\">Voir</a>
                    <a href=\"{{ path('program_edit', {'id': program.id}) }}\">Editer</a>
                </td>
            </tr>
        {% else %}
            <tr>
                <td colspan=\"5\">Aucune correspondance</td>
            </tr>
        {% endfor %}
        </tbody>
    </table>

    <a href=\"{{ path('program_new') }}\">Create new</a>
{% endblock %}
", "program/index.html.twig", "C:\\Users\\Romain\\Documents\\Questsymfony\\templates\\program\\index.html.twig");
